var in_upload = false;
function set_uploadify(inputid, hiddenid, file_limit, queueID, success, cancel_callback, file_types, button_txt, button_width, button_height, cancel_func) {
    if (file_limit == undefined)
        file_limit = 1;
    check_flash();
    o_datatype = $('#' + hiddenid).attr("datatype");
    if (o_datatype == "*")
        $('#' + hiddenid).attr("datatype", "file_upload_must");
    else {
        $('#' + hiddenid).attr("datatype", "file_upload");
        $('#' + hiddenid).attr("nullmsg", " ");
        $('#' + hiddenid).attr("errormsg", " ");
    }

//    var buttonClass = button_class || '';
    var width = button_width || '130';
    var height = button_height || '30';
    var file_type_txt = file_types || '*.*';
    var buttonText = button_txt || '上传附件';
    var swfuploadify = $('#' + inputid).uploadify({

        'auto': true, //是否自动上传,
        'buttonText': buttonText, //按钮文字
//            'buttonClass': buttonClass,
        'height': height, //按钮高度
        'width': width, //按钮宽度
        'checkExisting': false,//是否检测图片存在,不检测:false
        'fileObjName': 'imgFile',
        'fileSizeLimit': '1536MB', //文件大小限制 0为无限制 默认KB
        'fileTypeExts': file_type_txt,
        //'fileTypeDesc': 'All Files', //图片选择描述
        // 'formData'		:{ 'albumid':$("#albumid").val() },
        //'overrideEvents'	: ['onUploadProgress'],  // The progress will not be updated
        //'progressData'	: 'speed',             //默认percentage 进度显示方式
        'queueID': queueID, //默认队列ID
        'removeCompleted': false, //完成时是否清除队列 默认true
        'requeueErrors': false, //队列上传出错，是否继续回滚队列

        'swf': '/assets/uploadify-02bfe47ce09ed03f1fccba1a7f4a03d3dfb63330a3fe7c87668921fcdfdfd82b.swf', //swfUpload
        'uploader': '/attachments',

        //删除时触发
        'onCancel': function (event, queueId, fileObj, data) {
            //alert(111111);
            in_upload = false;
        },
        'onSelect': function (file) {
            var queue_count = Object.keys(this.queueData.files).length;
            if (queue_count > file_limit) {
                this.cancelUpload(file.id);
                delete file;
                $("#" + file.id).remove();
                alert("文件数量超过" + file_limit + "个！");
                return false;
            }
        },
        'onUploadStart': function () {
            in_upload = true;
        },
        //上传成功
        'onUploadSuccess': function (file, data, response) {
            in_upload = false;
            //alert(file.name + ' | ' + response + ':' + data);
            var att_id = eval("(" + data + ")").attachment_id;
            var val_old = $("#" + hiddenid).val();
            if (val_old.length > 0 && file_limit > 1)
                $("#" + hiddenid).val(val_old + "," + att_id);
            else
                $("#" + hiddenid).val(att_id);

            var cancel = $("#" + file.id + " .cancel a");
            if (cancel) {
                cancel.attr("attachment_id", att_id);
                cancel.attr("file_id", file.id);
                cancel.on('click', function () {
                    cancel_func();
                    var dvalue = $("#" + hiddenid).val();
                    $("#" + hiddenid).val(remove_from(dvalue, $(this).attr("attachment_id")));
                    //在这此处处理...
                    //通过uploadify的settings方式重置上传限制数量
                    //var uplimit = swfuploadify.uploadify('settings','uploadLimit');
                    //swfuploadify.uploadify('settings', 'uploadLimit', uplimit + 1);
                    delete window['uploadify_' + swfuploadify.attr("id")].queueData.files[$(this).attr("file_id")];
                    //防止手快多点几次x，把x隐藏掉
                    $(this).hide();
                    cancel_callback();
                });
            }
            if(success != undefined)
                success(); //回调
        }

    });
}

