function add_attachment_id(attachment_id, $file_input) {
    if ($file_input.val() == '' || $file_input.val() == undefined) {
        $file_input.val(attachment_id);
        return
    }
    ids_arr = $file_input.val().split(',');
    if (!(ids_arr.include(attachment_id))) {
        ids_arr.push(attachment_id);
        $file_input.val(ids_arr.join(','));
    }
}

function remove_attachment_id(attachment_ids, remove_id) {
    var attr_id_arr = attachment_ids.split(',');
    var indx = attr_id_arr.indexOf(remove_id);
    attr_id_arr.splice(indx, 1);
    return attr_id_arr.join(',');
}

