// 批量下载，去后台打包处理  仅适用于 Attachment
function batch_down(e, url) {
    var file_urls = [];
//    var excel_urls = [];
    var tab_index = 0;
    var tab_ids = [];
    $result_tab = $(e).closest('.result-table');
    $result_tab.find('.down-btn').each(function () {
        file_urls.push($(this).attr("attr_id"));
    });

//    $result_tab.find('.project-file-item:not(.excel-item) .down-btn').each(function () {
//        file_urls.push($(this).attr("attr_id"));
//    });
//    $result_tab.find('.project-file-item.excel-item .down-btn').each(function () {
//        excel_urls.push($(this).attr("attr_id"));
//    });
    var $operation_div = $(e).parent();
    var down_operation_html = $operation_div.html();
    try {
        $('.result-table').each(function (index) {
            tab_ids.push(this.id);
        });
        tab_ids = tab_ids.reverse();
        tab_index = tab_ids.indexOf($result_tab[0].id);
//        $(e).parent().append('<span style="color:#f66626;">正在为您打包文件，请等待...</span>')
//
        $operation_div.html('<span style="color:#f66626;">正在为您打包文件，请稍等(0)...</span>');
        var wait = 0;
        var stop_code = setInterval(function () {
            wait += 1;
            $operation_div.html('<span style="color:#f66626;">正在为您打包文件，请稍等(' + wait + ')...</span>');
        }, 1000);

        $.ajax({
            type: 'post',
            url: url + ".json",
            sync: true,
            data: {zip_index: tab_index + 1, attr_ids: file_urls.join(",")},
//            data: {zip_index: tab_index + 1, attr_ids: file_urls.join(","), excel_ids: excel_urls.join(",")},
            dataType: 'json',
            success: function (back) {
                clearInterval(stop_code);
                location.href = '/projects/batch_down?zip_path=' + back['zip_path'] + '&zip_name=' + back['zip_name']
                $operation_div.html(down_operation_html);
            },
            error: function (err) {
                alert("打包过程中出现错误。请刷新页面重新打包！");
                clearInterval(stop_code);
                $operation_div.html(down_operation_html);
            }
        });
    } catch (e) {

    }
}


