var dbclick_edit = function(obj, tab, col, target_id, input_type, need_cb) {
    var text_was = $(obj).text();
var input_obj = $('<input type="' + input_type + '" onblur="ajax_update_col(this)" value="' + text_was +'">')
    $(obj).attr({obj_id: target_id, tab_name: tab, col_name: col, need_cb: need_cb, text_was: text_was});
    $(obj).html(input_obj);
    input_obj.select();
    $(obj).removeClass('can_dbclick_edit');
}

var ajax_update_col = function (obj) {
    var p_obj = $(obj).parent();
    var to_val = $(obj).val();
    $.ajax({
        type: 'post',
        url: '/home/update_by_tab_col_id',
        data: {tab: p_obj.attr('tab_name'), col: p_obj.attr('col_name'),
            obj_id:  p_obj.attr('obj_id'), need_cb: p_obj.attr('need_cb'), val: to_val},
        dataType: 'json',
        success: function (result, status, xhr) {
            if(result){
//                window.location.reload();
                p_obj.addClass('can_dbclick_edit');
                p_obj.html(to_val);
            }else{
                alert('更新失败！联系开发人员！！！');
            }
        },
        error: function (xhr, s, err) {
            alert('更新失败！联系开发人员！！！');
        }
    })
}


