function auto_scroll($p_e, speed){
    var $e = $p_e.find('.dynamic-item:last');
    var init_marginTop = 0-($p_e[0].scrollHeight-$p_e.parent().height());
    $p_e.css({
        marginTop: init_marginTop
    });
    $p_e.animate({
        marginTop: init_marginTop+$e.height()
    },speed-200, function(){
        $p_e.prepend(
            $e.remove()
        );
        $p_e.css({
            marginTop: init_marginTop
        });
    });
    setTimeout(function(){
        auto_scroll($p_e, speed);
    }, speed);
}
