(function() {
  $(function() {
    return $('.project-impress').find('span').on('click', function() {
      var column, this_span;
      if ($("#project_impress_user_id").val() === '') {
        login();
        return;
      }
      this_span = $(this);
      column = this_span.attr('column');
      return $('#new_project_impress').ajaxSubmit({
        data: {
          "click_column": column
        },
        success: function(result) {
          if (result.msg == null) {
            if (parseInt(result[column]) === 1) {
              this_span.find('em').html(parseInt(this_span.find('em').html()) + parseInt(result[column]));
              this_span.addClass("checked");
            } else if (parseInt(result[column]) === 0) {
              this_span.find('em').html(parseInt(this_span.find('em').html()) - 1);
              this_span.removeClass("checked");
            }
          }
          return $('#new_project_impress').resetForm();
        }
      });
    });
  });

}).call(this);
