function iframeAutoFit(minHeight) {
    try {
        if (window != parent) {
            var a = parent.document.getElementsByTagName("IFRAME");
            for (var i = 0; i < a.length; i++) //author:meizz
            {
                if (a[i].contentWindow == window) {
                    var h1 = 0, h2 = 0;
                    a[i].parentNode.style.height = a[i].offsetHeight + "px";
                    a[i].style.height = "10px";
                    if (document.documentElement && document.documentElement.scrollHeight) {
                        h1 = document.documentElement.scrollHeight;
                    }
                    if (document.body) h2 = document.body.scrollHeight;

                    var h = Math.max(h1, h2);
                    if (document.all) {
                        h += 4;
                    }
                    if (window.opera) {
                        h += 1;
                    }
                    if (h <= 4) {
                        setTimeout("iframeAutoFit();", 500);
                        break;
                    }
                    if (minHeight && h < minHeight) h = minHeight;
                    a[i].style.height = a[i].parentNode.style.height = h + "px";
                }
            }
        }
    }
    catch (ex) {
    }
}


var set_chunk_uploader = function (uploader_id, btn_id, field_id, only_img, only_one, only_docx) {
    only_docx = only_docx || false;
    var file_md5, last_chunk, uploader;
    WebUploader.Uploader.unRegister("web_uploader");
    //
    WebUploader.Uploader.register({
        "name": "web_uploader",
        "before-send-file": "beforeSendFile",
        "before-send": "beforeSend",
        "after-send-file": "afterSendFile"
    }, {
        //1
        beforeSendFile: function (file) {
            var deferred = WebUploader.Deferred();
            //1
            (new WebUploader.Uploader()).md5File(file, 0, 10 * 1024 * 1024)
                .progress(function (percentage) {
                    $('#item1').find("p.state").text("...");
                })
                .then(function (val) {
                    file_md5 = val;
                    uploader.options.formData.file_md5 = file_md5;
                    $('#item1').find("p.state").text("...");
                    //
                    $.ajax({
                        type: "GET",
                        url: "/attachments/get_last_chunk_by_md5",
                        data: {
                            //
                            file_md5: file_md5
                        },
                        dataType: "json",
                        success: function (response) {
                            console.log(response);
                            last_chunk = response.last_chunk;
                            last_chunk_size = response.last_chunk_size;
                            deferred.resolve();
                        }
                    });
                });
            return deferred.promise();
        },
        //2
        beforeSend: function (block) {
            var chunk = block.chunk;
            var chunk_size = block.end - block.start;
            var deferred = WebUploader.Deferred();
            if (last_chunk > chunk) {
                deferred.reject();
            }
            $.ajax({
                type: "POST",
                url: "/attachments/check_chunk",
                data: {
                    file_md5: file_md5, //
                    chunk: chunk, //
                    chunk_size: chunk_size //
                },
                dataType: "json",
                success: function (response) {
                    if (response.file_exist === 1) {
                        //
                        deferred.reject();
                    } else {
                        //
                        deferred.resolve();
                    }
                }
            });
            return deferred.promise();
        },
        //3
        afterSendFile: function (res_data) {
            //
            $.ajax({
                type: "POST",
                url: "/attachments/merge_chunks",
                data: {
                    file_md5: file_md5,
                    file_name: res_data.name,
                    file_ext: res_data.ext,
                    file_size: res_data.size
                },
                success: function (res) {
                    if (res.state === 'ok') {
                        var attachment = eval("(" + res['attachment'] + ")");
                        var att_id = attachment.id;
                        var val_old = $("#" + field_id).val();
                        if (only_one) {
                            $("#" + field_id).val(att_id);
                        } else {
                            if (val_old) {
                                $("#" + field_id).val(val_old + "," + att_id);
                            } else {
                                $("#" + field_id).val(att_id);
                            }
                        }

                        $('#' + res_data.id).find('p.state').text('');
                        $('#' + res_data.id).find('p.state').css({
                            color: '#35966b'
                        });
                        $('#' + res_data.id).find('a.remove-file-btn').attr('att_id', att_id);
                    } else {
                        $('#' + res_data.id).find('p.state').text('');
                        $('#' + res_data.id).find('p.state').css({
                            color: '#d52b30'
                        });
                    }
                }
            });
        }
    });
    var file_limit = undefined;
    if (only_one) {
        file_limit = 1;
    }
    var option = {
        // swf
        swf: 'Uploader.swf',
        // 
        server: "/attachments/upload_chunk",
        // 
        // inputflash.
//        pick: {
//            id: '#'+btn_id,  //idid
//            multiple: false
//        },
        // image, jpeg
        threads: 1, //
        resize: false,
        auto: true, //
        //
        chunked: true,
        chunkSize: 10 * 1024 * 1024,
        fileNumLimit: file_limit
    }
    if (only_img) {
        option['accept'] = {
            title: 'Images',
            extensions: 'gif,jpg,jpeg,bmp,png',
            mimeTypes: 'image/*'
        };
    }
    if (only_docx) {
        option['accept'] = {
            title: 'docx|xlsx',
            extensions: 'docx,xlsx',
            mimeTypes: 'application/vnd.openxmlformats-officedocument.wordprocessingml.document,application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
        }
    }
    if (only_one) {
        option['pick'] = {
            id: '#' + btn_id,
            multiple: false
        };
    } else {
        option['pick'] = {
            id: '#' + btn_id,
            multiple: true
        }
    }

    uploader = WebUploader.create(option);

    // 
    uploader.on('fileQueued', function (file) {
        if (only_one) {
            $("#" + uploader_id + " .uploader-list").html(
                '<div id="' + file.id + '" class="file-item">' +
                '   <div>' + file.name + '</div>' +
                '   <p class="state"></p>' +
                '   <a href="javascript: void(0);" att_id="" class="remove-file-btn"></a>' +
                '   <div class="progress-percent">0%</div>' +
                '</div>');
            $('#' + file.id).find('a.remove-file-btn').on('click', function () {
                remove_file(this, file.id, uploader);
            });
        } else {
            $("#" + uploader_id + " .uploader-list").append(
                '<div id="' + file.id + '" class="file-item">' +
                '   <div>' + file.name + '</div>' +
                '   <p class="state"></p>' +
                '   <a href="javascript: void(0);" att_id="" class="remove-file-btn"></a>' +
                '   <div class="progress-percent">0%</div>' +
                '</div>');
            $('#' + file.id).find('a.remove-file-btn').on('click', function () {
                remove_file(this, file.id, uploader);
            });
        }

    });

    // 
    uploader.on('uploadProgress', function (file, percentage) {
        var $li = $('#' + file.id),
            $percent = $li.find('.progress .progress-bar');
        // 
        if (!$percent.length) {
            $percent = $('<div class="progress progress-striped active">' +
                '<div class="progress-bar" role="progressbar" style="width: 0%">' +
                '</div>' +
                '</div>').appendTo($li).find('.progress-bar');
        }
        $li.find('p.state').text('...');
        $percent.css('width', percentage * 100 + '%');
        $li.find('div.progress-percent').html(parseInt(percentage * 100) + '%');
    });

    uploader.on('uploadSuccess', function (file) {
        // $( '#'+file.id ).find('p.state').text('');
    });

    uploader.on('uploadError', function (file) {
        $('#' + file.id).find('p.state').text('');
    });

    uploader.on('uploadComplete', function (file) {
        //
        // $( '#'+file.id ).find('.progress').fadeOut();
    });

    // function start(){
    //     uploader.upload();
    //     $('#ctlBtn').attr("onclick","stop()");
    //     $('#ctlBtn').text("");
    // }
    //
    // function stop(){
    //     uploader.stop(true);
    //     $('#ctlBtn').attr("onclick","start()");
    //     $('#ctlBtn').text("");
    // }
}

